import { useEffect } from "react";
import { useState } from "react";

import { getAll } from "./BooksAPI";
function CurrentRead( books, setBooks){
    const options =[
        {
            value : 'currentlyReading',
            label: 'Currently Reading'

        },
        {
          value: 'wantToRead',
          label: 'Want To Read'
        },
        {
          value: 'read',
          label: 'Read'
        },
        {
          value: 'none',
          lable: 'None'
        }
      ]
const [selectedOption, setSelectedOption] = useState(options[3].value);
const handleOnchange = (book) => (e) =>{
setSelectedOption(e.target.value);
if(selectedOption == 'currentlyReading'){
setCurrentReadBook([...currentReadBook, book]);
setBooks(books.slice(0, -1));
}
if(selectedOption == 'wantToRead'){
setWantToReadBook([...wantToReadBook, book]);
setBooks(books.slice(0, -1));
}
if(selectedOption == 'read'){
setReadBook([...readBook, book]);
setBooks(books.slice(0, -1));
}
}
useEffect(()=>{
getAll().then((res) => ( setBooks(res),console.log(res)));
},[])
return (
<div className="search-books">
<div className="search-books-bar">
<a
    className="close-search"
    onClick={() => displaySearchPage(true)}
>
    Close
</a>
<div className="search-books-input-wrapper">
    <input
    type="text"
    placeholder="Search by title, author, or ISBN"
    />
</div>
</div>
<div className="search-books-results">
<ol className="books-grid">
    {books.map((book, index)=>(
        <li key = {index}>
          <div className="book">
            <div className="book-top">
              <div
                className="book-cover"
                style={{
                  width: 128,
                  height: 193,
                  backgroundImage:
                  `url(${book.imageLinks.thumbnail})`
                }}
              ></div>
              <div className="book-shelf-changer">
                <select value = {selectedOption} onChange = {handleOnchange(book)}>
                  {
                    options.map(o=>{
                      <option key = {o.value} value = {o.value}>{o.label}</option>
                    })
                  }
                </select>
              </div>
            </div>
            <div className="book-title">{`${book.title}`}</div>
            <div className="book-authors">{`${book.authors}`}</div>
          </div>
        </li>
    ))}

</ol>
</div>
</div>
)
}

export default CurrentRead
